import logo from './logo.svg';
import './Colors.css';
import React, {Component} from "react";
import { TrackFeatures } from 'react-spotify-api'

class Colors extends Component {
  constructor(props){
    super(props)

    this.state = {
      display1:"block",
      display2:"none",
      hue: "",
      saturation: "",
      lightness: "",
      color: "",
      id: "",
      link: ""
    }
  }

  handleLinkChange(e){
    this.setState({
      link: e.target.value
    });
  }

findColor = (songKey, tempo, energy) => { //fetching from api, based on those values, change state of 

  // fetch("google.com").then((resp)=>{
  //   console.log(resp)
  // }).catch((e)=>{
  //   console.log(e)
  // })
  var link = this.state.link;
  console.log(link);
  var songID = link.split("/")[4];
  songID = songID.split("?")[0];

  this.setState({id: songID});

  console.log(songID);

  if(songKey <= 0){
    this.setState({hue: 0})
  }
  else if(songKey > 0 && songKey <= 1){
    this.setState({hue: 30})
  }
  else if(songKey > 1 && songKey <= 2){
    this.setState({hue: 60})
  }
  else if(songKey > 2 && songKey <= 3){
    this.setState({hue: 90})
  }
  else if(songKey > 3 && songKey <= 4){
    this.setState({hue: 120})
  }
  else if(songKey > 4 && songKey <= 5){
    this.setState({hue: 150})
  }
  else if(songKey > 5 && songKey <= 6){
    this.setState({hue: 180})
  }
  else if(songKey > 6 && songKey <= 7){
    this.setState({hue: 210})
  }
  else if(songKey > 7 && songKey <= 8){
    this.setState({hue: 240})
  }
  else if(songKey > 8 && songKey <= 9){
    this.setState({hue: 270})
  }
  else if(songKey > 9 && songKey <= 10){
    this.setState({hue: 300})
  }
  else if(songKey > 10 && songKey <= 11){
    this.setState({hue: 330})
  }
  else if(songKey > 11 && songKey <= 12){
    this.setState({hue: 360})
  }

  if(tempo > 0 && tempo <= 80){
    this.setState({saturation: 20})
  }

  else if(tempo > 80 && tempo <= 100){
    this.setState({saturation: 35})
  } 

  else if(tempo > 100 && tempo <= 150){
    this.setState({saturation: 40})
  } 

  else if(tempo > 150 && tempo <= 170){
    this.setState({saturation: 60})
  } 

  else if(tempo > 150 && tempo <= 190){
    this.setState({saturation: 80})
  }

  else if(tempo > 190 && tempo <= 1000){
    this.setState({saturation: 100})
  }

  if(energy > 0 && energy <= 0.1){
    this.setState({lightness: 10})
  }

  else if(energy > 0.1 && energy <= 0.2){
    this.setState({lightness: 20})
  }

  else if(energy > 0.2 && energy <= 0.3){
    this.setState({lightness: 30})
  }

  else if(energy > 0.3 && energy <= 0.4){
    this.setState({lightness: 40})
  }
  else if(energy > 0.4 && energy <= 0.5){
    this.setState({lightness: 50})
  }
  else if(energy > 0.5 && energy <= 0.6){
    this.setState({lightness: 60})
  }
  else if(energy > 0.6 && energy <= 0.7){
    this.setState({lightness: 70})
  }
  else if(energy > 0.7 && energy <= 0.8){
    this.setState({lightness: 80})
  }
  else if(energy > 0.8 && energy <= 0.9){
    this.setState({lightness: 90})
  }
  else if(energy > 0.9 && energy <= 1.0){
    this.setState({lightness: 100})
  }

  this.setState({color: `hsl(${(this.state.hue)}, ${(this.state.saturation)}%, ${(this.state.lightness)}%)`});
  this.setState({display1:"none"});
  this.setState({display2: "block"});
}

goBack = () => {
  this.setState({color: "rgb(226, 213, 198)"});
  this.setState({display2: "none"});
  this.setState({display1: "block"});
}
  render() {
    return(
      <div className="Colors" style={{backgroundColor: this.state.color}}>
     <div className="Colors-box" style={{display: this.state.display1}}> 
       <p className="p">spotify colors</p>
        <input type="text" id="link" name="link" value={this.state.link} onChange={(e) => this.handleLinkChange(e)} placeholder="insert a spotify link!"></input> <br/>
        <TrackFeatures id={this.state.id}>
    {(features, loading, error) => (
        features ? (
          <button onClick={()=>this.findColor()} class="button">find the color</button>
        ) : null
    )}
</TrackFeatures>

      </div>
      <div className="Results-box" style={{display: this.state.display2}}>
       <p className="p">spotify colors</p>
       <p className="p1" >your hue is {this.state.hue}  <br/>
        your saturation is {this.state.saturation}  <br/>
        your lightness is {this.state.lightness}  <br/></p> 

          <button onClick={()=>this.goBack()} class="button">go back</button>
      </div>
    </div>
    )
  }
}

export default Colors;
